class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int value=0;
        for(int i=1;i<k;i++)
        if(colors[i]!=colors[i-1]) value++;

        if(value==k-1) ans++;
        int n=colors.size();

        for(int i=0;i<n-1;i++)
        {
            if(colors[(i+k)%n]!=colors[(i+k-1)%n]) value++;
            if(colors[i+1]!=colors[i]) value--;

            if(value==k-1) ans++;
        }

        return ans;
    }
};
