class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
         List<Integer> arr= new ArrayList<>();
        for (int it : colors) {
            arr.add(it);
        }
        for (int i = 0; i < k - 1; i++) {
            arr.add(colors[i]);
        }
        int ans = 0;
        for (int i = 0; i < arr.size(); i++) {
            int count = 1;
            for (int j = i + 1; j < arr.size(); j++) {
                if (arr.get(j) != arr.get(j - 1)) {
                    count++;
                } else {
                    i = j - 1;
                    break;
                }
                if (j == arr.size() - 1) {
                    i = 3 * arr.size();
                    break;
                }
            }
            ans += Math.max(0, count - k + 1);
        }
        return ans;
    }
}
