class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        L = len(colors)
        colors = colors + colors

        @cache
        def alter(b):
            if b > 0 and alter(b-1):
                return colors[b+k-1]!=colors[b+k-2]

            for i in range(b,b+k-1 , 1):
                if colors[i]== colors[i+1]:
                    return False

            return True

        n = 0
        for i in range(L):
            if alter(i):
                n+=1
        return n
