#define vi vector<int>
#define rep(i,a,b) for(int i=a;i<b;i++)
#define repr(i,a,b) for(int i=a;i>=b;i--)
#define nl cout<<"\n"


class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        vi arr;
        int n=col.size();
        rep(i,0,n){
            arr.push_back(col[i]);
        }
        rep(i,0,k-1)arr.push_back(col[i]);
        int ans=0;
        int temp=1;
        rep(i,1,arr.size()){
            if(arr[i]!=arr[i-1])temp++;
            else temp=1;
            if(temp>=k)ans++;
        }
        return ans;
    }
};
