class Solution {
public:
    vector<int> pec(string& p) {
        int m = p.size();
        vector<int> lps(m, 0);
        int len = 0;
        int i = 1;

        while (i < m) {
            if (p[i] == p[len]) {
                len++;
                lps[i] = len;
                i++;
            } else {
                if (len != 0) {
                    len = lps[len - 1];
                } else {
                    lps[i] = 0;
                    i++;
                }
            }
        }

        return lps;
    }

    int mec(string& text, string& p) {
        int n = text.size();
        int m = p.size();
        vector<int> lps = pec(p);
        int i = 0, j = 0;
        int count = 0;

        while (i < n) {
            if (p[j] == text[i]) {
                i++;
                j++;
            }

            if (j == m) {
                count++;
                j = lps[j - 1];
            } else if (i < n && p[j] != text[i]) {
                if (j != 0) {
                    j = lps[j - 1];
                } else {
                    i++;
                }
            }
        }

        return count;
    }

    string rec(int k, bool alt) {
        string p;
        for (int i = 0; i < k; ++i) {
            p += (alt ? '0' : '1');
            alt = !alt;
        }
        return p;
    }

    string tostr(vector<int>& colors) {
        string str;
        for (int color : colors) {
            str += (color == 0 ? '0' : '1');
        }
        return str;
    }

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        string p1 = rec(k, true);
        string p2 = rec(k, false);
        string str = tostr(colors);
        string dstr = str + str.substr(0, k - 1);

        int count1 = mec(dstr, p1);
        int count2 = mec(dstr, p2);

        return count1 + count2;
    }
};
