class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();  
        int count = 0;  

        if (k == 1) return n;  

        int j = 1;  
        int i = 1;  
        while (i < n + k - 1) {  
            int curr = i;  
            int prev = i - 1;  

            if (curr >= n) curr -= n;
            if (prev >= n) prev -= n;  
            if (colors[curr] != colors[prev]) {  
                ++j;  
            } else {  
                j = 1;  
            }
            if (j >= k) ++count;  
            ++i;  
        }  

        return count;  

    }
};
