class Solution {
public:
  int numberOfAlternatingGroups(vector<int> &color_arr, int target)
{
    int n = color_arr.size();

    int start = 0;
    int end = 1;

    int final_ans = 0;

    while (end < n + target && start < n)
    {
        int it = end;
        int temp_end = it % n;
        int newend=temp_end - 1 + n;

        int first = color_arr[temp_end];
        int second = color_arr[newend% n];

        int cmp=n + target - 1;

        while (first != second && it <cmp)
        {
            it++;
            temp_end = it % n;

            first = color_arr[temp_end];
            second = color_arr[(temp_end - 1 + n) % n];
        }

        final_ans += max(it - start - target + 1, 0);
        start = it;
        end = start + 1;
    }

    return final_ans;
}
};
