class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        for(int i=0;i<k-1;i++)
        {
            colors.push_back(colors[i]);
        }
        int count=0,n=colors.size();
        int left=0,right=0;
        for(int i=0;i<n-1;i++)
        {
            if(colors[right]!=colors[right+1])
            {
                right=(right+1);
            }
            else
            {
                left=(right+1);
                right=right+1;
            }
            if(right-left==k-1)
            {
                count++;
                left++;
            }
        }
        return count;
    }
};
