class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& ar, int k) {
        int n = ar.size();

        vector<int> cur(n);

        for(int i=1; i<n; i++){
            if(ar[i-1]==ar[i])cur[i-1] = 0;
            else cur[i-1] = 1;
        }
        if(ar[0]==ar[n-1])cur[n-1] = 0;
        else cur[n-1] = 1;

        int total = 0;
        for(int i=0; i<n; i++){
            total += cur[i];
        }
        if(total==n)return n;

        int cnt = 0;
        int pos = -1;
        for(int i=0; i<n; i++){
            if(cur[i]==1){
                cnt++;
                pos = i;
            }
            else break;
        }
        int sum = 0;
        int res = 0;
        k--;
        for(int i=pos+1; i<n; i++){
            if(cur[i]==1){
                sum++;
            }
            else{
                res += max(sum-k+1, 0);
                sum = 0;
            }
        }
        sum += cnt;
        res += max(sum-k+1, 0);

        return res;

    }
};
