class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int nums[n << 1];
        for (int i = 0; i < n << 1; i++)  nums[i] = colors[i % n];
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int j = i + 1;
            while (j < n << 1 && nums[j] != nums[j - 1])  j++;
            int l = j - i;
            ans += min(max(0, l - k + 1), n - i);
            i = j - 1;
        }
        return ans;
    }
};
