class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int num_found = 0;
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            j = findNextAlternatingEnd(arr, i, j, n, k);
            num_found += max(j - i - k + 1, 0);
            i = j;
            j = i + 1;
        }

        return num_found;
    }

private:
    int findNextAlternatingEnd(const vector<int>& arr, int start, int j, int n, int k) {
        int temp_j = j;
        int temp_end = temp_j % n;

        while (arr[temp_end] != arr[(temp_end - 1 + n) % n] && temp_j < (n + k - 1)) {
            temp_j += 1;
            temp_end = temp_j % n;
        }

        return temp_j;
    }
};
