class Solution {
public:
    int dp1[200010];
    int dp2[200010];
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        dp1[0]=1;
        for(int i=1;i<colors.size();i++){
            if(colors[i]!=colors[i-1]){
                dp1[i]=dp1[i-1]+1;
            }else dp1[i]=1;
        }
        dp2[colors.size()-1]=1;
        for(int i=colors.size()-2;i>=0;i--){
            if(colors[i]!=colors[i+1]){
                dp2[i]=dp2[i+1]+1;
            }else dp2[i]=1;
        }
        int cnt=0;
        for(int i=k;i<=n+k-1;i++){
            if(dp1[i]>=k) cnt++;
        }
        return cnt;
    }
};
