class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans = 0;
        int[] dp = new int[n + k - 1];
        dp[0] = 1;
        for (int i = 1; i < n + k - 1; i++) {
            dp[i] = colors[i % n] == colors[(i - 1) % n] ? 1 : dp[i - 1] + 1;
            if (dp[i] >= k) ans++;
        }
        return ans;
    }
}
