class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        i0 = -1
        ans = 0
        for i in range(n + k - 1):
            if i and colors[i - 1] == colors[i]:
                i0 = i - 1
            if i - i0 >= k:
                ans += 1
        return ans
