class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& ar, int k) {
        int n = ar.size();
        vector<int> cnt(n,0ll);
        int ans = 0;
        for(int i = 0; i < n; i++)
        {
            if(i == 0) 
            {
                // cnt[0] = 1;
                continue;
                // if(ar[i] != ar[n-1]) cnt[i] = 1;
            }
            else if(ar[i] != ar[i-1]) cnt[i] = 1;
        }



        vector<int> pre,suff;
        pre = cnt;
        suff = cnt;
        for(int i = 1; i < n; i++) pre[i] += pre[i-1];
        for(int i = n-2; i >= 0; i--) suff[i] += suff[i+1];

        for(int i = 0 ; i < n - k + 1; i++)
        {
            int r = pre[i + k - 1];
            if(i != 0) r -= pre[i-1];
            if(cnt[i] == 0) r++;
            if(r == k) ans++;
        }

        if(ar[0] != ar[n-1]) cnt[0] = 1;
        //  for(auto it: cnt) cout<<it<<" ";
        // cout<<endl;
        pre = cnt;
        suff = cnt;
        for(int i = 1; i < n; i++) pre[i] += pre[i-1];
        for(int i = n-2; i >= 0; i--) suff[i] += suff[i+1];

        for(int i = n-k+1; i < n; i++)
        {
            int r = pre[(i+k-1)%n] + suff[i];
            // cout<<pre[(i+k-1)%n] <<" "<< suff[i]<<endl;
            if(cnt[i] == 0) r++;
            if(r == k) ans++;
        }
        return ans;

    }
};
