class Solution {
    public int numberOfAlternatingGroups(int[] color, int k) {
        int count =0;
        int n = color.length;
        int len =0;
        int arr[] = new int[n];
        arr[n-1]=1;
        int prev = color[n-1];
        for(int i=n-2;i>=0;i--){
            if(prev != color[i]){
                arr[i]=arr[i+1]+1;
            }else{
                arr[i]=1;
            }
            prev = color[i];
        }
        int max =arr[0];
        prev = color[n-1]; 
        if(color[n-1] != color[0]){
            arr[n-1] += arr[0];
        }
        if(arr[n-1] >= k) count++;
        for(int i=n-2;i>=0;i--){
            int num = arr[i];
            if(color[i] != color[i+1]){
                arr[i] = arr[i+1]+1;
            }
            if(arr[i] >= k) count++;
        }

        return count;
    }
}
