class Solution {
    public int numberOfAlternatingGroups(int[] col, int k) {
        int m = col.length;
        int n = 2*m;
        int c[] = new int[n];
        for(int i = 0; i < n; i++){
            c[i] = col[i%m];
        }
        int e[] = new int[n];
        int o[] = new int[n];
        for(int i = 0; i < n; i++){
            if(i%2 == 0){
                e[i] = c[i]+(i-1 >= 0 ? e[i-1] : 0);
                o[i] = (i-1 >= 0 ? o[i-1] : 0);
            }
            else{
                e[i] = (i-1 >= 0 ? e[i-1] : 0);
                o[i] = c[i]+(i-1 >= 0 ? o[i-1] : 0);
            }
        }
        int cnt = 0;
         int l = k;
        k--;

        for(int i = 0; i < m; i++){
            if(i+k < n){
                if(i%2 == 0){
                    if(c[i] == 1){
                        int sum1 = e[i+k]-(i-1 >= 0 ? e[i-1] : 0);
                        int sum2 = o[i+k]-(i-1 >= 0 ? o[i-1] : 0);
                        // System.out.println(sum1+" "+sum2);
                        if(sum1 == (k+2)/2 && sum2 == 0) cnt++;
                    }
                    else{
                        int sum1 = e[i+k]-(i-1 >= 0 ? e[i-1] : 0);
                        int sum2 = o[i+k]-(i-1 >= 0 ? o[i-1] : 0);
                        // System.out.println(sum1+" "+sum2);
                        if(sum1 == 0 && sum2 == (k+1)/2) cnt++;
                    }
                }
                else{
                    if(c[i] == 1){
                        int sum1 = e[i+k]-(i-1 >= 0 ? e[i-1] : 0);
                        int sum2 = o[i+k]-(i-1 >= 0 ? o[i-1] : 0);
                        // System.out.println(sum1+" "+sum2);
                        if(sum2 == (k+2)/2 && sum1 == 0) cnt++;
                    }
                    else{
                        int sum1 = e[i+k]-(i-1 >= 0 ? e[i-1] : 0);
                        int sum2 = o[i+k]-(i-1 >= 0 ? o[i-1] : 0);
                        // System.out.println(sum1+" "+sum2);
                        if(sum2 == 0 && sum1 == (k+1)/2) cnt++;
                    }

                }
            }
            // System.out.println(cnt);
        }
        return cnt;
    }
}
