class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        new_colors = colors + colors
        segs = []
        temp = []
        for i in range(n * 2):
            if len(temp) == 0 or new_colors[i] != new_colors[i - 1]:
                temp.append(new_colors[i])
            else:
                segs.append(temp)
                temp = [new_colors[i]]
        if len(temp) > 0:
            segs.append(temp)
        i1, i2 = 0, 0
        num_groups = 0
        for i in range(n):
            l = len(segs[i1]) - i2
            if l >= k:
                num_groups += 1
            i2 += 1
            if i2 == len(segs[i1]):
                i1 += 1
                i2 = 0
        return num_groups
