class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int j=0;
        int x=a[0]^1,ans=0;
        vector<int> v;
        for(int i=0;i<=n;i++){
            if((i<n && a[i]==x) || i==n){
                // cout<<i<<endl;
                int t=i-j;
                v.push_back(t);
                // cout<<t<<endl;
                if(t>=k) ans+=(t-k+1);
                j=i;
            }else x^=1;
        }

        if(a[0]==a.back()) return ans;

        x=a[n-1]^1; int cnt=0;
        for(int i=n-1;i>=0;i--){
            if(a[i]==x) break;
            else cnt++,x^=1;
        }

        for(int i=k-1;i>0;i--){
            if(cnt>=i && v[0]>=k-i) ans++;
        }


        return ans;       

    }
};
