public class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int numGroups = 0;
        int startIdx = 0;
        int endIdx = 1;
        int length = colors.length;

        while (endIdx < length + k && startIdx < length) {
            int currentEndIdx = endIdx;
            int currentEnd = currentEndIdx % length;

            while (colors[currentEnd] != colors[(currentEnd - 1 + length) % length] && currentEndIdx < length + k - 1) {
                currentEndIdx++;
                currentEnd = currentEndIdx % length;
            }

            numGroups += Math.max(currentEndIdx - startIdx - k + 1, 0);
            startIdx = currentEndIdx;
            endIdx = startIdx + 1;
        }

        return numGroups;
    }


}
