class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors *= 2
        res = 0
        j = 0
        for i in range(n):
            j = max(j, i + 1)
            while j < i + k:
                if colors[j - 1] != colors[j]:
                    j += 1
                else:
                    break
            else:
                res += 1
        return res
