class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

    colors.insert(colors.end(), colors.begin(), colors.begin() + k);

    int n = colors.size();
    int ans = 0;
    vector<int> diff(n + 1, 0);

    for (int i = 1; i < n; i++) {
        diff[i] = diff[i - 1] + abs(colors[i] - colors[i - 1]);
    }

    k--;

    for (int i = k + 1; i < n; i++) {
        if (diff[i] - diff[i - k] == k) {
            ans++;
        }
    }

    return ans;
    }
};
