class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> arr(n+k-1);
        for(int i=0;i<n;i++)arr[i]=colors[i];
        for(int i=0;i<k-1;i++)arr[n+i]=colors[i];
        int ans=0;
        int z=1;
        for(int i=1;i<arr.size();i++){
            if(arr[i]!=arr[i-1])z++;
            else z=1;
            if(z>=k)ans++;
        }
        return ans;
    }
};
