class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        colors.insert(colors.end(),colors.begin(),colors.end());
        int l=0,ans=0;
        for(int r=0;r<2*n;r++){
            if(r&&colors[r]==colors[r-1]){
                l=r;
                if(l>=n)break;
            }else{
                if(r-l+1>=k){
                    ans+=1;
                    l++;
                    if(l>=n)break;
                }
            }
        }
        return ans;
    }
};
