class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();   
        int ans = 0; 
        vector<int>window(n, 0); 
        for(int i= 0; i<n; i++){
        int val = (colors[(i)%n]^colors[(i+1)%n]);
            window[i] = val;
        }

        k--; 
        int  i = 0 , j =0;  
        int size = n+k-1; 
        while(j <size){
        int s = i%n , e = j%n; 
         if(window[e] == 1){
             j++;
          if(j-i == k){
            ans++;
           i++;      
          }
         }
        else{
             j++; 
             i = j; 
        }
        }
        return ans;
    }
};
