class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int [] arr = new int [n + k - 1];
        arr[0] = 1;
        int prev = colors[0];
        for(int i = 1 ; i < arr.length ; i++) {
            int in = i % n;
            if(colors[in] == prev) arr[i] = 1;
            else arr[i] = arr[i - 1] + 1;
            prev = colors[in];
        }
        int count = 0;
        for(int i = 0 ; i < arr.length ; i++) {
            if(arr[i] >= k) count ++;
        }
        return count ;
    }
}
