class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        a.extend(a[:k-1])
        ans = 0
        i = curr = 1
        n = len(a)
        while i < n:
            if a[i] != a[i-1]:
                curr += 1
            else: 
                curr = 1
            if curr == k:
                curr -= 1
                ans += 1
            i += 1
        return ans
