public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) 
    {
        int ans = 0;
        for (int i = 0, j = 0; i < colors.Length; ++i) 
        {
            if (j < i) j = i;

            while (j - i + 1 < k) 
            {
                if (Get(j + 1) != Get(j)) ++j; else break;
            }

            if (j - i + 1 == k) ++ans; else 
            {
                i = j;
            }

            int Get(int x) => colors[x % colors.Length];
        }
        return ans;
    }
}
