class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int ans = 0;
        int n = arr.size();

        int cnt = 0;
        int pre = arr[0];
        for(int i=1;i<n;i++){
            if(arr[i]!=pre) cnt++;
            else cnt = 0;
            if(cnt>=k-1) ans++;
            pre = arr[i];
        }

        for(int i=0;i<k-1;i++){
            if(arr[i]!=pre) cnt++;
            else cnt = 0;
            if(cnt>=k-1) ans++;
            pre = arr[i];
        }
        return ans;
    }
};
