class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ok = a[0] ^ 1;
        int ans = 1,r=0;
        int n = a.size();
        for(int i = 1;i<a.size();i++){
            if(a[i] == ok){
                ans++; 
                if(ans == k) {
                    r++; ans = k-1;
                }
                ok ^= 1;
            }
            else {
                ans = 1;
            }
        }
        vector<int>p;
        for(int i = n-k+1;i<n;i++){
          p.push_back(a[i]);
        }
        for(int i = 0;i<k-1;i++){
          p.push_back(a[i]);
        }
        // rotate(a.begin(), a.begin()+n-k+1, a.end());
        ok = p[0]^1;
        ans = 1;
        for(int i = 1;i<p.size();i++){
            if(p[i] == ok){
                ans++; 
                if(ans == k) {
                    r++; ans = k-1;
                }
                ok ^= 1;
            }
            else {
                ans = 1;
            }
        }
        return r;
    }
};
