class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v = colors;
        int sz = (int)v.size();
        int pref[sz];
        int ans = 0;
        for (int i = 0; i < sz; i++) {
            if (i == 0) pref[i] = 1;
            else {
                pref[i] = pref[i-1];
                if (v[i] != v[i-1]) pref[i]++;
            }
        }
        for (int i = 0; i < sz; i++) {
            if (i+k > sz) {
                int baki = k-(sz-i)-1;
                int sum = pref[sz-1];
                if (i) sum -= pref[i-1];
                if (i && v[i] == v[i-1]) sum++;
                sum += pref[baki];
                if (v[0] != v[sz-1] && sum == k) {
                    ans++;
                }
            }
            else {
                int sum = pref[i+k-1];
                if (i) sum -= pref[i-1];
                if (i && v[i] == v[i-1]) sum++;
                if (sum == k) ans++;
            }
        }
        return ans;
    }
};
