class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0; 

        int count = 0, patternLength = 0;
        int expected = -1;

        for (int i = 0; i < n + k - 1; ++i) {
            int current = colors[i % n]; 
            if (current == expected) {
                patternLength++;
            } else {
                patternLength = 1;  
            }
            expected = 1 - current;  

            if (patternLength >= k) {
                count++;
            }
        }

        return count; 
    }
};
