class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
        int n = colors.size();
        if(k == 1) return n;

        int i=1;
        int len = 1;

        while(i < n + k - 1)
        {
            int curr = i;
            int prev = i-1;



            if(colors[curr %n] != colors[prev%n])
            {
                len++;
            }
            else
            {
                len = 1;
            }

            if(len >= k)
            {
                count++;
            }
            i++;
        }
        return count;
    }
};
