class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int x = 0;
        int n = colors.size();
        int i = 0, j = 1;


        while (i<n && j<n+k) {
            int st = j;
            int ed = st % n;

            while(st < (n + k - 1) && colors[(ed - 1 + n) % n] != colors[ed] ) 
            {
                st+=1;
                ed=st%n;
            }

            x+=max(st-i-k+1, 0);
            i=st;
            j=i+1;
        }

        return x;




    }
};
