class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int group_count = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while (j < n + k && i < n) {
            int temp1 = j;
            int tempEnd = temp1 % n;


            while (colors[tempEnd] != colors[(tempEnd - 1 + n) % n] &&
                   temp1 < n + k - 1) {
                temp1++;
                tempEnd = temp1 % n;
            }


            group_count += max(temp1 - i - k + 1, 0);
            i = temp1;
            j = i + 1;
        }

        return group_count;
    }
};
