class Solution {
public:
    int numberOfAlternatingGroups(std::vector<int>& colors, int k) {

        int n = colors.size();
        int alternating_count = 1;
        int result = 0;

        for(int i = 0; i < n + k - 2; i++){

            if(colors[i%n] != colors[(i+1)%n]){
                alternating_count++;
            }else{
                alternating_count = 1;
            }

            if(alternating_count >= k){
                result++;

            }
        }



        return result;
    }
};
