class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
         int n= colors.size();
         int count = 0;
          int alt=1;
        int i=1;
        while(i<n+k-1){
             int cur_pos = i;
            int prev_pos =i-1;

             if(cur_pos>=n){
                 cur_pos-=n;
             }
             if(prev_pos >=n){
                 prev_pos-=n;
             }
             if(colors[cur_pos]!=colors[prev_pos]){
                 alt++;
             }
            else{
                alt  = 1;
            }
            if(alt>=k){
                count++;
            }
            i++;
        }
        return count;
    }
};
