class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v = colors;
        int m = colors.size();
        for(auto it:colors) v.push_back(it);
        int n = v.size();
        int i=0;
        int j = 1;
        int count = 0;
        int c = 1;
        while(i<m && j<m+k){

            while(j<i+k){
                if(v[j]==v[j-1]){
                    j++;
                    i = j-1;
                    c=1;
                    break;
                }
                else{
                    c++;
                    j++;
                }
            }
            if(c==k){
                count++;
                c--;
                i++;
            }
            // i++;
        }
        return count;
    }
};
