class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for (int i = 0; i < k-1; i++) a.pb(a[i]);
        n = a.size();
        vi dp(n);
        int ans = 0;
        for (int i = 1; i < n; i++) dp[i] = a[i] != a[i-1];
        // debug(a);
        // debug(dp);
        for (int i = 1; i < n; i++) dp[i] += dp[i-1];
        for (int L = 0; L < n; L++) {
            int R = L + k - 1;
            if (R >= n) break;
            int sum = dp[R]-dp[L];
            ans += sum == R-L;
        }
        return ans;
    }
};
