class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();
        int j = 0;
        for(int i=0; i<n; i++){
            if(j-i!=k-1){
                j=i;
                int cnt = 1;
                while(cnt<k && colors[j%n]!=colors[(j+1)%n]){
                    j++;
                    cnt++;
                }
                if(cnt!=k){
                    continue;
                }
                ans++;
                j++;
                continue;
            }

            // cnt==k

            if((colors[(j)%n]==colors[i%n] && colors[(j-1+n)%n]!=colors[i%n]) || 
                (colors[(j)%n]!=colors[i%n] && colors[(j-1+n)%n]==colors[i%n])){
                // cout<<i<<" "<<j+1<<'\n';
                j++;
                ans++;
            }
        }
        return ans;
    }
};
