class Solution {
public:
int count(string& pat, string& txt)
        {
            int n = pat.size();
            vector<int> pi(n, 0);
            for(int i = 1; i<n; i++){
                if(pat[i] == pat[pi[i-1]]) pi[i] = pi[i-1] + 1;
                else{
                    int j = pi[i-1];
                    while(j > 0 && pat[i] != pat[j]){
                        j = pi[j-1];
                    }
                    if(j == 0){
                        if(pat[i] == pat[0]) pi[i] = 1;
                        else pi[i] = 0;
                    }
                    else pi[i] = j+1;
                }
            }
            vector<int> ans;
            int j = 0;
            for(int i = 0; i<txt.size(); i++){
                if(txt[i] == pat[j]){
                    j++;
                    if(j == pat.size()){
                        ans.push_back(i-pat.size()+2);
                        j = pi[j - 1];
                    }
                }
                else{
                    while(j > 0 && txt[i] != pat[j]){
                        j = pi[j-1];
                    }
                    if(txt[i] == pat[j]) j++;
                }
            }

            return ans.size();
        }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        string given = "";
        for(auto x : colors){
            if(x) given+='1';
            else  given += '0';
        }
        for(int i = 0; i<k-1; i++){
            if(colors[i]) given += '1';
            else given += '0';
        }

        string pat1 = "", pat2 = "";
        for(int i = 0; i<k; i++){
            if(i%2 == 0){
                pat1 += '0';
                pat2 += '1';
            }else{
                pat1 += '1';
                pat2 += '0';
            }
        }


        int ans = 0;
        ans += count(pat1, given);
        ans += count( pat2, given);
        return ans;
    }
};
