class Solution {
public:

    void fio(){
        ios::sync_with_stdio(false); // Speeds up input/output
        cin.tie(nullptr); // Unties cin from cout for faster input
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        fio();

        int a = colors.size();
        int cnt = 0;
        if (k == 1) {
            return a;
        }
        int oneskip_other = 1;
        for (int i = 1; i < a + k - 1; ++i){
            int c = i % a;
            int p = (i - 1) % a;
            if (colors[c]!= colors[p]) {
                ++oneskip_other;
            } else {
                oneskip_other = 1;
            }
            if (oneskip_other >= k) {
                ++cnt;
            }
        }

        return cnt;
    }
};
