class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size = colors.size(); 
        int times = 0; 
        int altLength = 1;

        if (k == 1) return size;

        for (int idx = 1; idx < size + k - 1; ++idx) { 
            int prevIdx = (idx - 1) % size; 
            int currIdx = idx % size; 
            if (colors[currIdx] != colors[prevIdx]) { 
                altLength++; 
            } 
            else { 
                altLength = 1; 
            } 
            if (altLength >= k) times++; 
        } 
        return times;  
    }
};
