class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        int i=0,j=1;
        int count = 0;
        while(i<n){
            bool flag=true;
            while(j-i+1<=k){
                if(colors[j]==colors[j-1]){
                    flag = false;
                    break;
                }
                j++;
            }
            if(flag){
                //cout<<i<<" ";
                count++;
                i++;
            }
            else{
                //cout<<"|||"<<i<<"-"<<j;
                i=j;
                j++;
            }
        }
        //cout<<endl;
        return count;

    }
};
