class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        for(int i=0;i<k-1;i++){
            arr.push_back(arr[i]);
        }

        int ans= 0;
        int n = arr.size();


        int count = 1;


        for(int i=1;i<n;i++){
            if(arr[i]!=arr[i-1]){
                count++;

                if(count==k){
                    ans++;
                    i++;
                    while(i<n && arr[i-1]!=arr[i]){
                        ans++;
                        i++;
                    }
                    count = 1;
                }
            }
            else {
                count=1;
            }
        }
return ans;
    }
};
