class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k)
    {
        int low=0 , high=1 , count=0 , n=colors.size();
        while(low<n)
        {
            if(high==0 && colors[0]==colors[n-1])
            {
                while(low!=high && low<n)
                {
                    low++;
                }
            }
            else if(high>0 && colors[high]==colors[high-1])
            {
                while(low!=high && low<n)
                {
                    low++;
                }
            }
            if((high>=low && high-low==k-1) || (high<low && high+n-low+1==k))
            {
                count++;
                low++;
            }
            high=(high+1)%n;
        }
        return count;
    }
};
