class Solution {
    func numberOfAlternatingGroups(_ colors: [Int], _ k: Int) -> Int {
        var numGroups = 1
        var alt = true
        var lastNonAltIndex = -1
        let len = colors.count

        var i = 0
        while i < (k - 1) {
            if colors[i] == colors[i + 1] {
                alt = false
                numGroups = 0
                lastNonAltIndex = i
                i += 1
                continue
            }
            i += 1
        }

        var j = 1
        while j < len {
            if alt && colors[(k - 2 + j) % len] != colors[(k + j - 1) % len] {
                numGroups += 1
            } else if alt && colors[(k - 2 + j) % len] == colors[(k + j - 1) % len] {
                alt = false
                lastNonAltIndex = k + j - 2
            } else if !alt && j <= lastNonAltIndex {
                if colors[(k - 2 + j) % len] == colors[(k + j - 1) % len] {
                    lastNonAltIndex = k + j - 2
                }
                j += 1
                continue
            } else if !alt && j > lastNonAltIndex {
                if colors[(k - 2 + j) % len] != colors[(k + j - 1) % len] {
                    numGroups += 1
                    alt = true
                } else {
                    lastNonAltIndex = k - 2 + j
                    alt = false
                }
            }
            j += 1
        }
        return numGroups
    }
}
