class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int last = -1, curr = 0;
        for (int i = 0; i < k; i++){
            if (colors[i] != last){
                curr++;
            }
            else{
                curr = 1;
            }
            last = colors[i];
        }
        int ans = 0;
        if (curr == k) ans++;
        for (int i = 1; i < n; i++){
            if (colors[(i + k - 1) % n] != last){
                curr++;
            }
            else{
                curr = 1;
            }
            // cout << curr << " " << last << " " << (i + k - 1) % n << "      ";
            if (curr >= k) ans++;
            last = colors[(i + k - 1) % n];
        }
        // cout << endl;
        return ans;
    }
};
