class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> extendedColors;
        for(auto color : colors) {
            extendedColors.push_back(color);
        }

        int extraElements = k - 1;
        for(auto color : colors) {
            extendedColors.push_back(color);
            extraElements--;
            if(extraElements == 0) break;
        }

        int groupCount = 0; 
        int start = 0;
        int end = 1;
        int currentLength = 1;

        while(end < extendedColors.size()) {
            if(extendedColors[end - 1] != extendedColors[end]) {
                currentLength++;
            } else {
                start = end;
                currentLength = 1;
            }

            if(end - start + 1 == k) {
                groupCount++;
                start++;
            }
            end++;
        }

        return groupCount;
    }
};
