class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i = 0; i < n; i++) {
            a.push_back(a[i]);
        }
        vector<int> dp(2 * n , 0);
        dp[0] = 1;
        int ans = 0;
        for(int i = 1; i < n + k - 1; i++) {
            if(a[i] != a[i - 1]) {
                dp[i] = dp[i - 1] + 1;
            }
            else {
                dp[i] = 1;
            }
            ans +=(dp[i] >= k);
        }
        return ans;
    }
};
